changeSet.sql:
CREATE TABLE gtp_bundle ( code character varying(10) PRIMARY KEY, notes character varying(250) );
CREATE TABLE gtp_section ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_title ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_chapter ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_sub_chapter ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_region ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_line ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_account_pec ( code character varying(10) PRIMARY KEY, name character varying(200) NOT NULL, notes character varying(250) );
CREATE TABLE gtp_warrant ( id BIGINT PRIMARY KEY, gross double precision NOT NULL, deduction double precision NOT NULL, debit_account_pec character varying(25), credit_account_pec character varying(25), date_pec date, title character varying(10) NOT NULL, section character varying(10) NOT NULL, chapter character varying(10) NOT NULL, sub_chapter character varying(10) NOT NULL, line character varying(10) NOT NULL, bundle character varying(10) NOT NULL, region character varying(10) NOT NULL, imputation character varying(50) NOT NULL, CONSTRAINT gtp_warrant_id_fkey FOREIGN KEY (id) REFERENCES pay_reference_document (id) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_debit_account_pec_fkey FOREIGN KEY (debit_account_pec) REFERENCES gtp_account_pec (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_credit_account_pec_fkey FOREIGN KEY (credit_account_pec) REFERENCES gtp_account_pec (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_title_fkey FOREIGN KEY (title) REFERENCES gtp_title (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_section_fkey FOREIGN KEY (section) REFERENCES gtp_section (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_chapter_fkey FOREIGN KEY (chapter) REFERENCES gtp_chapter (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_sub_chapter_fkey FOREIGN KEY (sub_chapter) REFERENCES gtp_sub_chapter (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_sub_line_fkey FOREIGN KEY (line) REFERENCES gtp_line (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_bundle_fkey FOREIGN KEY (bundle) REFERENCES gtp_bundle (code) ON UPDATE CASCADE ON DELETE CASCADE, CONSTRAINT gtp_warrant_region_fkey FOREIGN KEY (region) REFERENCES gtp_region (code) ON UPDATE CASCADE ON DELETE CASCADE );
CREATE VIEW gtp_warrant_view AS SELECT rd.*, wr.gross, wr.deduction, wr.debit_account_pec, wr.credit_account_pec, wr.date_pec, wr.title, wr.section, wr.chapter, wr.sub_chapter, wr.line, wr.bundle, wr.region, wr.imputation FROM gtp_warrant wr LEFT JOIN pay_reference_document_view rd ON rd.id = wr.id;
